describe('Testando pokemon cards', () => {
  test('Exibe a logo e o subtítulo "pesquise um pokemon"', () => {
    render(<App />);
    const imgLogo = screen.getByRole('img', { name: /logo/i });

    const subtitle = screen.getByRole('heading', { level: 3, name: /pesquise um Pokemon/i });

    expect(imgLogo).toBeInTheDocument();
    expect(subtitle).toBeInTheDocument();

  });

  test('Exibe o botão e o input', () => {

    render(<App />);

    const botao = screen.getByRole('button', { name: /pesquisar/i })

    const input = screen.getByRole('textbox');


    expect(botao).toBeInTheDocument();
    expect(input).toBeInTheDocument();
  });

  test('Consegue digitar no input', () => {
    // Acessar
    render(<App />);

    const input = screen.getByRole('textbox');

    // Agir
    userEvent.type(input, 'ditto');

    // Aferir
    expect(input).toHaveValue('ditto');
  });

  test('Exibe o pokemon e o card digitado, fazendo uma única chamada para a API', async () => {

    // global.fetch = async () => ({
    //   json: async () => mockRequestResult
    // });

    global.fetch = jest.fn(async () => ({
      json: async () => mockRequestResult
    }));

    // jest.spyOn(global, 'fetch');

    // Acessar
    render(<App />);

    const input = screen.getByRole('textbox');
    const botao = screen.getByRole('button', { name: /pesquisar/i });

    // Agir
    userEvent.type(input, 'pikachu');
    userEvent.click(botao);

    // Aferir

    const cardTitleEl = await screen.findByRole('heading', { name: /pikachu\-ex/i });
    const cardImg = screen.getByRole('img', { name: /pikachu\-ex\-card/i });

    expect(cardTitleEl).toBeInTheDocument();
    expect(cardImg).toBeInTheDocument();
    expect(global.fetch).toHaveBeenCalledWith("https://api.pokemontcg.io/v1/cards?name=pikachu");
    expect(global.fetch).toHaveBeenCalledTimes(1);
    screen.logTestingPlaygroundURL();

    // jest.restoreAllMocks()
  })

});


